    <script>
        // topBtn 滑上至top scroll up
        $(function () {
            $(window).scroll(function () {
                var target = $(".topBtn");
                var isVisible = target.is(":visible");
                if ($(window).scrollTop() >= 600) {
                    if (!isVisible) target.show();
                } else {
                    if (isVisible) target.hide();
                }
            });
            $(".topBtn").on('click',function (event) {
                $('html,body').animate({
                    scrollTop: '0'
                },1000);
            });
        });
    </script>
    <script>
        // 按鈕狀態
        $(function () {
            switch ('{{ $btnStatus }}') {
                // 顯示我要登記按鈕 & 顯示下載APP視窗
                case 'popupBox':
                    $('#register-btn').attr('data-fancybox', '');
                    $('#register-btn').attr('data-src', '#reg-app');
                    $('#done-btn').hide();
                    break;
                // 顯示已登記按鈕
                case 'finish':
                    $('#register-btn').hide();
                    break;
                // 顯示我要登記按鈕，並可登記
                case 'sign':
                    $('#register-btn').on("click", sign);
                    $('#done-btn').hide();
                    break;
            }
        });

        // 我要登記
        function sign()
        {
            $.ajax({
                type: 'POST',
                url: '/api/arpu/signup',
                data: {
                    gm_uid : {{ $gmUid }},
                    t: '{{ $t }}'
                },
                success: function(res) {
                    if (res.return_code === '0000') {
                        swal('已登記完成！', '', 'success');
                    } else {
                        swal(res.description, '', 'error');
                        if (res.return_code === '9901') {
                            $('#done-btn').text('已額滿囉！');
                        }
                        $('#register-btn').hide();
                        $('#done-btn').show();
                    }
                }
            });
        }
    </script>
